mapper.update:

UPDATE HE_LostDogBoard 
SET readcount = readcount + 1
WHERE lostdogboard_no = #{lostdogboard_no}

mapper.select:

SELECT * FROM HE_LostDogBoard_ImageBoard 
WHERE lostdogboard_no = #{lostdogboard_no}
ORDER BY imageboard_no ASC

mapper.update:

