mapper.update:

update HE_Member
set id = #{id}
where member_no = #{member_no}


update HE_Member
set nick = #{nick}
where member_no = #{member_no}


update HE_Member
set birth = #{birth}
where member_no = #{member_no}


update HE_Member
set petName = #{petName}
where member_no = #{member_no}

update HE_Member
set address = #{address},
postCode = #{postCode}
where member_no = #{member_no}

mapper.select:

select count(*) from HE_Member
where pw = #{pw}
and member_no = #{member_no}

mapper.update:

update HE_Member
set pw = #{pw}
where member_no = #{member_no}

update HE_Member
set email = #{email}
where member_no = #{member_no}
